public string ContentType
{
    get { return MimeType.GetMimeType(fileSystemInfo.Extension) ?? "application/octet-stream"; }
}

public long ContentLength
{
    get { return fileInfo.Length; }
}

remarks.param:
By default ASP.NET buffers content on server side before sending output. You must turn off buffering to
eliminate keeping entire file content in memory before sending:

HttpContext.Current.Response.BufferOutput = false;

example.code:
public virtual async Task ReadAsync(Stream output, long startIndex, long count)
{
    //Set timeout to maximum value to be able to download large files.
    HttpContext.Current.Server.ScriptTimeout = int.MaxValue;
    if (ContainsDownloadParam(context.Request.RawUrl))
    {
        AddContentDisposition(Name);
    }

    byte[] buffer = new byte[bufSize];
    using (FileStream fileStream = fileInfo.Open(FileMode.Open, FileAccess.Read, FileShare.Read))
    {
        fileStream.Seek(startIndex, SeekOrigin.Begin);
        int bytesRead;
        int toRead = (int)Math.Min(count, bufSize);
        if (toRead <= 0)
        {
            return;
        }

        try
        {
            bytesRead = await fileStream.ReadAsync(buffer, 0, toRead);
            while (bytesRead  > 0)
            {
                await output.WriteAsync(buffer, 0, bytesRead);
                count -= bytesRead;
                bytesRead = await fileStream.ReadAsync(buffer, 0, toRead);
            }
        }
        catch (HttpException)
        {
            // The remote host closed the connection (for example Cancel or Pause pressed).
        }
    }
}

public virtual async Task<bool> WriteAsync(Stream content, string contentType, long startIndex, long totalFileSize)
{
    await RequireHasTokenAsync();
    //Set timeout to maximum value to be able to upload large files.
    HttpContext.Current.Server.ScriptTimeout = int.MaxValue;
    if (startIndex == 0 && fileInfo.Length > 0)
    {
        using (FileStream filestream = fileInfo.Open(FileMode.Truncate)) { }
    }
    await fileInfo.SetExtendedAttributeAsync("TotalContentLength", totalFileSize >= 0 ? (object)totalFileSize : null);
    await fileInfo.SetExtendedAttributeAsync("SerialNumber", this.serialNumber + 1);

    using (FileStream fileStream = fileInfo.Open(FileMode.OpenOrCreate, FileAccess.Write, FileShare.Read))
    {
        if (fileStream.Length < startIndex)
        {
            throw new DavException("Previous piece of file was not uploaded.", DavStatus.PRECONDITION_FAILED);
        }

        if (!content.CanRead)
        {
            return true;
        }

        fileStream.Seek(startIndex, SeekOrigin.Begin);
        byte[] buffer = new byte[bufSize];

        int lastBytesRead;
        try
        {
            lastBytesRead = await content.ReadAsync(buffer, 0, bufSize);
            while (lastBytesRead > 0)
            {
                await fileStream.WriteAsync(buffer, 0, lastBytesRead);
                await fileStream.FlushAsync();
                lastBytesRead = await content.ReadAsync(buffer, 0, bufSize);
            }
        }
        catch (HttpException)
        {
            // The remote host closed the connection (for example Cancel or Pause pressed).
        }
    }
    await context.socketService.NotifyRefreshAsync(GetParentPath(Path));
    return true;
}

public string Etag
{
    get { return string.Format("{0}-{1}", Modified.ToBinary(), this.serialNumber); }
}

member.example:
            Example of CopyToAsync implementation for WebDAV Class 2 server:

The code below is part of 'WebDAVServer.FileSystemStorage.AspNet' C# & VB samples provided with the SDK.public override async Task CopyToAsync(IItemCollectionAsync destFolder, string destName, bool deep, MultistatusException multistatus)
{
    DavFolder targetFolder = (DavFolder)destFolder;

    if (targetFolder == null || !Directory.Exists(targetFolder.FullPath))
    {
        throw new DavException("Target directory doesn't exist", DavStatus.CONFLICT);
    }

    string newFilePath = System.IO.Path.Combine(targetFolder.FullPath, destName);
    string targetPath = targetFolder.Path + EncodeUtil.EncodeUrlPart(destName);

    // If an item with the same name exists - remove it.
    try
    {
        IHierarchyItemAsync item = await context.GetHierarchyItemAsync(targetPath);
        if (item != null)
            await item.DeleteAsync(multistatus);
    }
    catch (DavException ex)
    {
        // Report error with other item to client.
        multistatus.AddInnerException(targetPath, ex);
        return;
    }

    // Copy the file togather with all extended attributes (custom props and locks).
    try
    {
        File.Copy(fileSystemInfo.FullName, newFilePath);

        // Locks should not be copied, delete them.
        if (await fileSystemInfo.HasExtendedAttributeAsync("Locks"))
            await new FileInfo(newFilePath).DeleteExtendedAttributeAsync("Locks");
    }
    catch (UnauthorizedAccessException)
    {
        // Fail
        NeedPrivilegesException ex = new NeedPrivilegesException("Not enough privileges");
        string parentPath = System.IO.Path.GetDirectoryName(Path);
        ex.AddRequiredPrivilege(parentPath, Privilege.Bind);
        throw ex;
    }
    await context.socketService.NotifyRefreshAsync(targetFolder.Path);
}
Public Overrides Async Function CopyToAsync(destFolder As IItemCollectionAsync, destName As String, deep As Boolean, multistatus As MultistatusException) As Task Implements IHierarchyItemAsync.CopyToAsync
    Dim targetFolder As DavFolder = CType(destFolder, DavFolder)
    If targetFolder Is Nothing OrElse Not Directory.Exists(targetFolder.FullPath) Then
        Throw New DavException("Target directory doesn't exist", DavStatus.CONFLICT)
    End If

    Dim newFilePath As String = System.IO.Path.Combine(targetFolder.FullPath, destName)
    Dim targetPath As String = targetFolder.Path & EncodeUtil.EncodeUrlPart(destName)
    ' If an item with the same name exists - remove it.
    Try
        Dim item As IHierarchyItemAsync = Await context.GetHierarchyItemAsync(targetPath)
        If item IsNot Nothing Then Await item.DeleteAsync(multistatus)
    Catch ex As DavException
        ' Report error with other item to client.
        multistatus.AddInnerException(targetPath, ex)
        Return
    End Try

    ' Copy the file togather with all extended attributes (custom props and locks).
    Try
        File.Copy(fileSystemInfo.FullName, newFilePath)
        ' Locks should not be copied, delete them.
        If Await fileSystemInfo.HasExtendedAttributeAsync("Locks") Then Await New FileInfo(newFilePath).DeleteExtendedAttributeAsync("Locks")
    Catch __unusedUnauthorizedAccessException1__ As UnauthorizedAccessException
        ' Fail
        Dim ex As NeedPrivilegesException = New NeedPrivilegesException("Not enough privileges")
        Dim parentPath As String = System.IO.Path.GetDirectoryName(Path)
        ex.AddRequiredPrivilege(parentPath, Privilege.Bind)
        Throw ex
    End Try

    Await context.socketService.NotifyRefreshAsync(targetFolder.Path)
End Function

            Example of MoveTo implementation for WebDAV Class 2 server:

The code below is part of 'WebDAVServer.FileSystemStorage.AspNet' C# & VB samples provided with the SDK.public override async Task MoveToAsync(IItemCollectionAsync destFolder, string destName, MultistatusException multistatus)
{
    await RequireHasTokenAsync();

    DavFolder targetFolder = (DavFolder)destFolder;

    if (targetFolder == null || !Directory.Exists(targetFolder.FullPath))
    {
        throw new DavException("Target directory doesn't exist", DavStatus.CONFLICT);
    }

    string newDirPath = System.IO.Path.Combine(targetFolder.FullPath, destName);
    string targetPath = targetFolder.Path + EncodeUtil.EncodeUrlPart(destName);

    // If an item with the same name exists in target directory - remove it.
    try
    {
        IHierarchyItemAsync item = await context.GetHierarchyItemAsync(targetPath);

        if (item != null)
        {
            await item.DeleteAsync(multistatus);
        }
    }
    catch (DavException ex)
    {
        // Report exception to client and continue with other items by returning from recursion.
        multistatus.AddInnerException(targetPath, ex);
        return;
    }

    // Move the file.
    try
    {
        File.Move(fileSystemInfo.FullName, newDirPath);

        // Locks should not be copied, delete them.
        var newFileInfo = new FileInfo(newDirPath);
        if (await newFileInfo.HasExtendedAttributeAsync("Locks"))
            await newFileInfo.DeleteExtendedAttributeAsync("Locks");
    }
    catch (UnauthorizedAccessException)
    {
        // Exception occurred with the item for which MoveTo was called - fail the operation.
        NeedPrivilegesException ex = new NeedPrivilegesException("Not enough privileges");
        ex.AddRequiredPrivilege(targetPath, Privilege.Bind);

        string parentPath = System.IO.Path.GetDirectoryName(Path);
        ex.AddRequiredPrivilege(parentPath, Privilege.Unbind);
        throw ex;
    }
    // Refresh client UI.
    await context.socketService.NotifyRefreshAsync(GetParentPath(Path));
    await context.socketService.NotifyRefreshAsync(targetFolder.Path);
}
Public Overrides Async Function MoveToAsync(destFolder As IItemCollectionAsync, destName As String, multistatus As MultistatusException) As Task Implements IHierarchyItemAsync.MoveToAsync
    Await RequireHasTokenAsync()
    Dim targetFolder As DavFolder = CType(destFolder, DavFolder)
    If targetFolder Is Nothing OrElse Not Directory.Exists(targetFolder.FullPath) Then
        Throw New DavException("Target directory doesn't exist", DavStatus.CONFLICT)
    End If

    Dim newDirPath As String = System.IO.Path.Combine(targetFolder.FullPath, destName)
    Dim targetPath As String = targetFolder.Path & EncodeUtil.EncodeUrlPart(destName)
    ' If an item with the same name exists in target directory - remove it.
    Try
        Dim item As IHierarchyItemAsync = Await context.GetHierarchyItemAsync(targetPath)
        If item IsNot Nothing Then
            Await item.DeleteAsync(multistatus)
        End If
    Catch ex As DavException
        ' Report exception to client and continue with other items by returning from recursion.
        multistatus.AddInnerException(targetPath, ex)
        Return
    End Try

    ' Move the file.
    Try
        File.Move(fileSystemInfo.FullName, newDirPath)
        ' Locks should not be copied, delete them.
        Dim newFileInfo = New FileInfo(newDirPath)
        If Await newFileInfo.HasExtendedAttributeAsync("Locks") Then Await newFileInfo.DeleteExtendedAttributeAsync("Locks")
    Catch __unusedUnauthorizedAccessException1__ As UnauthorizedAccessException
        ' Exception occurred with the item for which MoveTo was called - fail the operation.
        Dim ex As NeedPrivilegesException = New NeedPrivilegesException("Not enough privileges")
        ex.AddRequiredPrivilege(targetPath, Privilege.Bind)
        Dim parentPath As String = System.IO.Path.GetDirectoryName(Path)
        ex.AddRequiredPrivilege(parentPath, Privilege.Unbind)
        Throw ex
    End Try

    ' Refresh client UI.
    Await context.socketService.NotifyRefreshAsync(GetParentPath(Path))
    Await context.socketService.NotifyRefreshAsync(targetFolder.Path)
End Function

